import CSS from "csstype";

export const inputStyles: CSS.Properties = {
    backgroundColor: '#f5f4f0',
    textAlign: 'center',
    border: '1px',
    borderRadius: '4px',
    padding: '0.5rem',
    width: '100%',
    fontSize: '14px',
    color: '#011228',
};

export const buttonStyles: CSS.Properties = {
    backgroundColor: '#7395aa',
    margin: '5px',
    border: '0px',
    borderRadius: '2px',
    fontSize: '14px',
    color: '#FDFFFC',
    padding: '6px',
    boxShadow: 'rgba(27, 31, 35, .3) 0 1px 0',
    verticalAlign: 'middle',
    cursor: 'pointer'
};

export const tableStyles: CSS.Properties = {
    boxShadow: 'rgba(9, 30, 66, 0.2) 0px 1px 1px, rgba(9, 30, 66, 0.1) 0px 0px 1px 1px',
};
