import CSS from "csstype";

export const formStyles: CSS.Properties = {
    textAlign: 'center',
    marginTop: '20px',
};

export const cardStyles: CSS.Properties = {
    display: 'inline-block',
    width: '30%',
    boxShadow: 'rgba(0, 0, 0, 0.1) 0px 2px 8px',
};

export const labelStyles: CSS.Properties = {
    marginRight: '5px',
};

export const inputStyles: CSS.Properties = {
    backgroundColor: '#f5f4f0',
    textAlign: 'center',
    border: '1px',
    borderRadius: '4px',
    padding: '0.5rem',
    fontSize: '14px',
    color: '#011228',
};

export const buttonStyles: CSS.Properties = {
    backgroundColor: '#7395aa',
    margin: '5px',
    border: '0px',
    borderRadius: '4px',
    fontSize: '14px',
    width: '80%',
    color: '#FDFFFC',
    padding: '6px',
    verticalAlign: 'middle',
    cursor: 'pointer'
};
