import React, {useContext} from 'react';
import 'antd/dist/antd.css';
import {Button} from 'antd';
import {observer} from "mobx-react";
import {FranchiseTypeModelType, PlayerTypeModelType, StoreContext} from "../../../models";
import {simButtonStyles} from "./SimButtonStyles";
import {mutateCreatePlayerQuery} from "../../Utils/queries";

export const DraftButton: React.FunctionComponent = observer(() => {

        const store = useContext(StoreContext)


        const draftSimRepeat = (franchise: FranchiseTypeModelType, player: any) => {

            let numOfTeamsBeforeUser = 0
            let currentTeamPosition = store.User.league.draftOrder.indexOf(franchise)
            // let currentTeamPosition = store.User.league.draftOrder.indexOf(franchise)
            let userTeamPosition =  store.User.league.draftOrder.indexOf(store.User.franchise)
            // let numOfTeamsBeforeUser = 0
            if (userTeamPosition > currentTeamPosition) {
                numOfTeamsBeforeUser = userTeamPosition - currentTeamPosition
            }
            else{
                numOfTeamsBeforeUser = Math.abs(store.User.league.franchiseSet.length - currentTeamPosition + userTeamPosition)
            }
            let playersRemaining = store.User.league.draftClass.filter((player: PlayerTypeModelType) => {
                if (player.year === 1 && player.franchise == null)
                    return player
            })
            console.log(playersRemaining)
            console.log(numOfTeamsBeforeUser)
            if (playersRemaining < numOfTeamsBeforeUser) {
                numOfTeamsBeforeUser = playersRemaining
            }
            for (let i = 0; i < numOfTeamsBeforeUser; i++) {
                draftRepeater(i);
            }

            function draftRepeater(i: any) {
                setTimeout(function () {
                    franchise = store.User.league.draftingFranchise
                    player = store.User.league.bestDraftPlayer
                    console.log(franchise.franchise, " ", player.name)
                    draftSim(franchise, player)
                }, 1000 * i);
            }
        }

        const draftSim = (franchise: FranchiseTypeModelType, player: any) => {
            store.mutateCreatePlayer({
                    "playerInput": {
                        "name": player.name,
                        "suit": player.suit,
                        "age": player.age,
                        "pv": player.pv,
                        "epv": player.epv,
                        "sEpv": player.sEpv,
                        "contract": undefined,
                        "tOption": undefined,
                        "pOption": undefined,
                        "renew": undefined,
                        "salary": undefined,
                        "grade": undefined,
                        "lineup": "bench",
                        "franchiseId": store.User.franchise.league.draftingFranchise.id,
                        "trainer": false,
                        "year": player.year,
                        "leagueId": store.User.franchise.league.id
                    }
                }, mutateCreatePlayerQuery,
                undefined
            )
            try {
                // make next franchise in draft order draftingFranchise
                store.User.league.setDraftingFranchise(store.User.league.draftOrder[store.User.league.draftOrder.indexOf(franchise) + 1])
            } catch (exception_var) {
                // if at the end of draft order reset to beginning
                store.User.league.setDraftingFranchise(store.User.league.draftOrder[0])
            }
        }


        return (
            <div>
                {store.User.league.bestDraftPlayer ?
                    <Button style={simButtonStyles}
                            onClick={() => draftSimRepeat(store.User.league.draftingFranchise, store.User.league.bestDraftPlayer)}
                            block>
                        Draft Sim
                    </Button> :
                    <h1 style={{textAlign: 'center'}}>
                        Go to OffSeason
                    </h1>
                }
            </div>
        );
    }
)

export default DraftButton;
