import React, { Component} from "react";

class Counter extends Component {
    state = {
        value: this.props.value,
        tags: ['tags1', 'tags2', 'tags3']
    };

    styles = {
        fontSize: '20px',
        fontWeight: 'bold'
    };

    renderTags() {
        if (this.state.tags.length === 0) return <p>'There are not tags!'</p>;

        return <ul>{ this.state.tags.map(tag => <li key={tag}>{ tag }</li>) }</ul>
    }

    handleIncrement = (state) => {
        this.setState({value: this.state.value + 1});
        console.log(this)
    };



    render() {
        console.log(this.props);

        return (
            <div>
                {this.props.children}
                <span style={this.styles} className={this.getBadgeClasses()}>{this.formatValue()}</span>
                <button onClick={ () => this.handleIncrement()} style={this.styles} className={"btn btn-secondary btn-md"}>Increment</button>
                <button style={this.styles} className="btn btn-danger btn-md">Delete</button>
                {this.state.tags.length === 0 && "Please create a new tag!"}
                {this.renderTags()}
            </div>
        );
    }

    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.value === 0) ? "warning" : "primary";
        return classes;
    }

    formatValue() {
        const {value} = this.state;
        return value === 0 ? <h1>Zero</h1> : value;
    }
}


export default Counter;
