      <form onSubmit={this.handleSubmit}>
          <input className={"field"} type="text" value={this.state.value} onChange={this.handleChange} />
        <input type="submit" value="Submit" className={"btn btn-secondary btn-md m-2"} />
         <DropdownCity
            title="Select fruit"
            list={this.state.fruit}
            resetThenSet={this.resetThenSet}
          />
      </form>
    );
  }
}

export default DropdownCity;
