import React, { Component} from "react";

class Counter extends Component {
    state = {
        count: 0,
        tags: ['tags1', 'tags2', 'tags3']
    };

    styles = {
        fontSize: '10px',
        fontWeight: 'bold'
    };

    renderTags() {
        if (this.state.tags.length === 0) return <p>'There are not tags!'</p>;

        return <ul>{ this.state.tags.map(tag => <li key={tag}>{ tag }</li>) }</ul>
    }

    handleIncrement = () => {
        this.state.count++;
        this.setState(this.state.count + 1)
    };

    checkIncrement = () => {
        const { count } = this.state;
        if (count === 0) {
            console.log ("counter is zero")
        } else {
            console.log("counter is NOT zero")
        }
    };

    render() {
        return (
            <div>
                <span style={this.styles} className={this.getBadgeClasses()}>{this.formatCount()}</span>
                <button onClick={this.handleIncrement} className={"btn btn-secondary btn-md"}>Increment</button>
                {this.state.tags.length === 0 && "Please create a new tag!"}
                {this.renderTags()}
            </div>
        );
    }

    getBadgeClasses() {
        let classes = "badge m-2 badge-";
        classes += (this.state.count === 0) ? "warning" : "primary";
        return classes;
    }

    formatCount() {
        const {count} = this.state;
        return count === 0 ? <h1>Zero</h1> : count;
    }
}


export default Counter;
