}

  return (
    <div>
      <form
      onSubmit={event => {
        event.preventDefault();

        saveDrafted(value);
        reset();
      }}
    >
      <TextField
        variant="outlined"
        placeholder="Add Player"
        margin="normal"
        onChange={onChange}
        value={value}
      />
    </form>
          <select onChange={onChange}>
        <option>Select Player</option>
                {dropdown}
        </select>
       <button onClick={draftPlayer}> Draft</button>
    </div>
  );
};

export default DraftedForm;
