import React, { useState } from "react";
import ReactDOM from 'react-dom';
import './index.css';
import Header from './components/Header';
import Login from './components/Login';
import Home from './components/Home';
import Draft from './components/Draft';
import Stadium from './components/Stadium';
import GM from './components/GM';
import Season from './components/Season';
import OffSeason from './components/OffSeason';

import { BrowserRouter as Router, Switch, Route} from 'react-router-dom';

// exporting to login component
export const AuthContext = React.createContext();

//this is our initial state that will be in the reducer.
// Checks if user is authenticated, contains user data, and if a token was sent back
const loginState = {
  isAuthenticated: false,
  user: null,
  token: null,
};
// reducer function. contains case-switch statement that, based on actions, returns new state
const reducer = (state, action) => {
  switch (action.type) {
    //  type of action when dispatched with payload (user and token) it saves to localStorage
      //  and returns new state for isAuth, user, and token
    case "LOGIN":
      localStorage.setItem("user", JSON.stringify(action.payload.user));
      localStorage.setItem("token", JSON.stringify(action.payload.token));
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token
      };
    //  type of action when dispatched clears localStorage
    case "LOGOUT":
      localStorage.clear();
      return {
        ...state,
        isAuthenticated: false,
        user: null
      };
    default:
      return state;
  }
};

function App() {
  //  if no action is dispatched return initial State
  const [state, dispatch] = React.useReducer(reducer, loginState);
  return (
          <div className='App'>
    <AuthContext.Provider
      value={{
        state,
        dispatch
      }}
    >
      <Header />
      <div className="App">{!state.isAuthenticated ? <Login /> : <Home />}</div>
    </AuthContext.Provider>


      <div className='Content'>
        <h4>In Progress...</h4>
      <Router>
      <Switch>
      <Route exact path='/Home' component={Home} />
      <Route exact path='/Stadium' component={Stadium} />
      <Route exact path='/GM' component={GM} />
      <Route exact path='/Draft' component={Draft} />
      <Route exact path='/Stadium' component={Stadium} />
      <Route exact path='/Season' component={Season} />
      <Route exact path='/OffSeason' component={OffSeason} />
      </Switch>
      </Router>
      </div>
    </div>
  );
}

export default App
