import React, {useEffect, useState} from "react";
import axios from "axios";

function CustomSelect(props) {
  const [city] = useState(props.city);
  // const [selectedCity, updateSelectedCity] = useState("");
  const[selectedCity, updateSelectedCity] = useState([])

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/citychoice/')
        .then(res => {
          console.log(res)
          selectedCity(res.city)
        })
        .catch(err => {
          console.log(err)
    })
  }, [])

  function handleChange(event) {
    updateSelectedCity(event.target.value);
    if (props.onSelectChange) props.onSelectChange(selectedCity);
  }
  let options = city.map(city => (
    <option key={city.id} value={city.id}>
      {city.name}
    </option>
  ));
  return (
    <select
      name="customSearch"
      className="custom-search-select"
      onChange={handleChange}
    >
      <option>Select Item</option>
      {options}
    </select>
  );
}
export default CustomSelect;
