import React, {useEffect, useState} from 'react';
import axios from "axios";

export default function Draft() {
  return <DataFetching />;
}

function DataFetching() {
  const[player, setPlayer] = useState([])
  const[value, setValue] = useState("");

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/playergeneration/')
        .then(res => {
          console.log(res)
          setPlayer(res.data)
        })
        .catch(err => {
          console.log(err)
    })
  }, [])

  let dropdown = player.map(player => (
    <option key={player.id} value={player.suit}>
      {player.name}
    </option>
  ));


  const [salary, setSalary] = useState("");

    // Function to add salary and update salary in state
  function calculateSalary() {
    setSalary(player.name);
    console.log(player.name);
  }

  return (
      <div>
        <p>
          {
            player.map(player => <p key={player.id}>Name: {player.name} Suit: {player.suit} Age: {player.age} Player Value: {player.pv}</p>)
          }
        </p>
        <select onChange={e => setValue(e.currentTarget.value)}>
        <option>Select Player</option>
                {dropdown}
        </select>
          <p></p>
     <button onClick={calculateSalary}>Calculate</button>
    <h3>{value}</h3>
      </div>
  )
}
