import React, {useState, useEffect} from 'react';
import axios from "axios";

function Signing() {
  const [contractlength, setContractLength] = React.useState([
    {label: "One Year", value: 1},
    {label: "Two Years", value: 2},
    {label: "Three Years", value: 3},
    {label: "Four Years", value: 4},
    {label: "Five Years", value: 5}
  ]);

   const[selected, setSelected] = useState([]);

    const [contractrenewal] = React.useState([
    {label: "No", value: "no"},
    {label: "Non-Repeat", value: "non-repeat"},
    {label: "Repeat", value: "repeat"},
  ]);
    //Make this a conditional select based on contract length?
    const [teamoption] = React.useState([
    {label: "None", value: null},
    {label: "Year One", value: 1},
    {label: "Year Two", value: 2},
    {label: "Year Three", value: 3},
    {label: "Year Four", value: 4},
  ]);
    //Make this a conditional select based on contract length?
    const [playeroption] = React.useState([
    {label: "None", value: null},
    {label: "Year One", value: 1},
    {label: "Year Two", value: 2},
    {label: "Year Three", value: 3},
    {label: "Year Four", value: 4},
  ]);

  let dropdown = contractlength.map(contractlength => (
        <option key={contractlength.value} value={contractlength.value}>
        {contractlength.label}
        </option>
  ));

  return (
    <div>

        <span>Select: Contract Length</span>
        <select onChange={e => setSelected(e.currentTarget.value)}>
        <option>Select Contract Length</option>
                {dropdown}
        </select>

        <span>Contract Renewal</span>
    <select>
      {contractrenewal.map(contractrenewal => (
        <option key={contractrenewal.value} value={contractrenewal.value}>
          {contractrenewal.label}
        </option>
      ))}
    </select>

        <span>Player Option</span>
    <select>
      {playeroption.map(playeroption => (
        <option key={playeroption.value} value={playeroption.value}>
          {playeroption.label}
        </option>
      ))}
    </select>
      </div>
  );
}

export default Signing
