import React, { Component } from "react";
import DropdownCity from "./DropdownCity";

class NameForm extends Component {
  constructor(props) {
    super(props);
    this.state = {value: 'Please Enter Your Team Name', location:
    [
        {
          id: 0,
          title: 'Los Angeles',
          selected: false,
          key: 'location'
        },
        {
          id: 1,
          title: 'New York',
          selected: false,
          key: 'location'
        },
        {
          id: 2,
          title: 'Boston',
          selected: false,
          key: 'location'
        },
        {
          id: 3,
          title: 'Miami',
          selected: false,
          key: 'location'
        },
        {
          id: 4,
          title: 'Phoenix',
          selected: false,
          key: 'location'
        }
      ]};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

    toggleSelected = (id, key) => {
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp[id].selected = !temp[id].selected
    this.setState({
      [key]: temp
    })
  }

  resetThenSet = (id, key) => {
    let temp = JSON.parse(JSON.stringify(this.state[key]))
    temp.forEach(item => item.selected = false);
    temp[id].selected = true;
    this.setState({
      [key]: temp
    })
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
          <input className={"field"} type="text" value={this.state.value} onChange={this.handleChange} />
          <div>''</div>
          <DropdownCity title="Select Location" list={this.state.location} resetThenSet={this.resetThenSet}/>
          <input type="submit" value="Submit" className={"btn btn-secondary btn-md m-2"} />

      </form>
    );
  }
}

export default NameForm;
